package ai.indxo;

public class Method {
	
	static int sum(int a, int b) {
		return a+b;
	}
	static int product(int a, int b) {
		return a*b;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(sum(10, 4));
		
		int ans = product(3, 5);
		System.out.println(ans);

	}

}
